import React from "react";
import { Grid, Typography } from "@material-ui/core";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import "../styles.scss";

const useStyles = makeStyles(theme => ({
  contact: {
    flexGrow: "1"
  },
  h2: {
    fontFamily: "Petit Formal Script",
    color: theme.palette.primary.main,
    fontWeight: 500
  }
}));

const Contact = () => {
  return (
    <Grid
      container
      justify="center"
      alignItems="center"
      direction="column"
      className={classes.contact}
    >
      <Typography variant="h2">Let's chat!</Typography>
      <p>
        <FontAwesomeIcon icon="envelope" /> goon.sarah&#64;gmail.com
      </p>
      <div>
        <a href="https://github.com/sarahg813" target="_blank">
          <FontAwesomeIcon className="contactIcons" icon={["fab", "github"]} />
        </a>
        <a href="https://linkedin.com/in/sarahgoon" target="_blank">
          <FontAwesomeIcon
            className="contactIcons"
            icon={["fab", "linkedin"]}
          />
        </a>
        <a href="https://angel.co/sarah-goon" target="_blank">
          <FontAwesomeIcon
            className="contactIcons"
            icon={["fab", "angellist"]}
          />
        </a>
      </div>
    </Grid>
  );
};

export default Contact;
