import React from "react";
import { Grid, Typography, Box, Paper } from "@material-ui/core";
import { makeStyles } from "@material-ui/core/styles";

const useStyles = makeStyles(theme => ({
  about: {
    flexGrow: "1",
    backgroundColor: "rgba(255, 255, 255, 0.9)",
    justify: "center",
    overflow: "auto"
  },
  item: {
    margin: "2em 5em"
  },
  text: {
    alignText: "center"
  },
  title: {
    fontFamily: "Arima Madurai",
    color: theme.palette.primary.dark
  }
}));

const About = () => {
  const classes = useStyles();
  return (
    <Grid container spacing={10}>
      <Grid item xs={12}>
        <Paper>
          <h4>Hi! I am a Full Stack Web Developer based in NYC.</h4>
          <p>
            The reason why I love developing is because of the gratifying
            results. Every little challenge you face while working on a project
            brings a great "woohoo!" when those challenges get solved.
          </p>
        </Paper>
      </Grid>
    </Grid>
  );
};

export default About;
